package String;

public class ENDSWITH {

	public ENDSWITH() {
		// TODO Auto-generated constructor stub
	}
	public static void main(String[] args) {
		String s="My name is";
		System.out.println(s.endsWith("is"));
		System.out.println(s.endsWith("s"));
		System.out.println(s.endsWith("S"));
		System.out.println(s.endsWith(" is"));
	}

}
