package String;

public class REPLACE {

	public REPLACE() {
		// TODO Auto-generated constructor stub
	}
	public static void main(String[] args) {
		String s="My name is Ram 123";
		System.out.println(s.replace('R','C'));
		System.out.println(s.replace("Ram","Rakesh b"));
		System.out.println(s.replaceAll("[A-z]",""));
		
			
		
	}

}
